import React,{useState} from 'react'
import styled from 'styled-components'



const Navbar = styled.div`
background-color: black;
color:white;
display:flex;
justify-content:space-between;
padding:1%;
width:100%;
`
const Way = styled.ul`
display:flex;
justify-content:space-around;
`
const Menu = styled.nav`
            height: 100vh;
            position: absolute;

            top: ${cssvalue};

            left: 0;

            z-index: 2;
            display: flex;
            align-items: center;
            justify-content: center;
            background-color: rgb(65, 64, 64);
            width: 100%;
            flex-direction: column;
            transition: 1s;
            text-align: center;

`




function Nav(){
const closemenu =()=>{
    setcssvalue('-100vh')
    console.log(cssvalue);
}
const openmenu =()=>{
    setcssvalue('100vh')
    console.log(cssvalue);
}
const [cssvalue, setcssvalue] = useState('-100vh');




return(
<>


<div class=" gayab">
            <div class="logo">
                IOE ASPIRANTS
            </div>
            <Menu cssvalue={cssvalue}>
                  <ul>
                  <li><a href="#" >HOME</a></li>
                  <li><a href="#" >ABOUT US</a></li>
                  <li><a href="#" >PAYMENT</a></li>
                  <li><a href="#" >CONTACT US</a></li>
                  
                  </ul>
                  <i class="fa fa-times fa-4x" aria-hidden="true" onClick={closemenu}></i>
            </Menu>
                  <i class="fa fa-bars fa-4x" aria-hidden="true" onClick={openmenu}></i>
</div>
    
    
<div class="navbar">
            <div class="logo">
            IOE ASPIRANTS
           </div>
            <div class="link">
                  <ul>
                        <li><a href="#">Home</a></li>
                        <li><a href="#">About Us</a></li>
                        <li><a href="#">Contact Us</a></li>
                        <li><a href="#">Payment</a></li>
                  </ul>

            </div>


  </div>





</>




)


}
export default Nav
