import React from 'react'
import Head from '../Home/Head.jsx'
import Middle from '../Home/Middle.jsx'
import Bottom from '../Home/Bottom.jsx'
import {useAnimation, motion} from 'framer-motion'
import {useInView} from 'react-intersection-observer'


function Home(){
  const anime ={
    hidden:{
      scale:1.2,
        opacity:0
    },
    show:{
        scale:1,
        opacity:1,
        Transition:{
            duration:1
        }
    }
    
    
    
    }
    
    
    const [element , view] = useInView();
    const control = useAnimation();
    console.log(view);
    if(view){
        control.start('show')
    }else{
        control.start('hidden')
    }





  const PageAnimation = {
    hidden:{
        opacity:0,
        y:300
    
    },
    show:{
        opacity:1,
        y:0,
        transition:{
            duration:1,
            when:"beforeChildren",
            staggerChildren:0.25,

        }
    },
     exit:{
      opacity:0,
      y:300,
transition:{
  duration:0.75,
}
    }
    
    }

return(
  <motion.div exit='exit' variants={PageAnimation} initial='hidden' animate='show'>
  <Head/>
  <Middle variants={anime} animate={control} initial='hidden' ref={element} />
  <Bottom  variants={anime} animate={control} initial='hidden' ref={element}/>
</motion.div>


)



}
export default Home
