import React,{useState, useEffect} from 'react'
import styled from 'styled-components'
import {Link} from 'react-router-dom'


const Navbar = styled.div`
background-color: black;
color:white;
display:flex;
justify-content:space-between;
padding:1%;
width:100%;
`
const Way = styled.ul`
display:flex;
justify-content:space-around;
`
const Menu = styled.nav`
            height: 100vh;
            position: absolute;

            top:0; 

            left: ${props=> props.cssvalue};
            overflow: hidden;
            z-index: 1;
            display: flex;
            align-items: center;
            justify-content: center;
            background-color: rgb(65, 64, 64);
            width: 30vw;
            flex-direction: column;
            transition: 0.6s;
            text-align: center;

`
const MenuUl= styled.ul`
display:flex;
flex-direction:column;

`
const Logo = styled.div`
color: white;
    font-family: ;
    width: 100%;
    text-align: center;
    font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;
    font-size: 2rem;
`



function Nav(){


const closemenu =()=>{
    setcssvalue('-30vw')
    console.log(cssvalue);
}
const openmenu =()=>{
    setcssvalue('0')
    console.log(cssvalue);
}
const [cssvalue, setcssvalue] = useState('-30vw');




return(
<>


<div class=" gayab">
      
            <Menu cssvalue={cssvalue}>
                  <MenuUl>
                  <li onClick={openmenu}><Link to="/" >HOME</Link></li>
                  <li onClick={openmenu}><Link to="/about" >ABOUT</Link></li>
                  <li onClick={openmenu} ><Link to="/note">NOTES</Link></li>
                  
                  </MenuUl>
                  <i class="fa fa-times fa-3x" aria-hidden="true" onClick={closemenu}></i>
            </Menu>
                  <i class="fa fa-bars fa-3x" aria-hidden="true" onClick={openmenu}></i>
            <Logo>
            IOE ASPIRANTS
      </Logo>
</div>
    
    
<div class="navbar">
            <div class="logo">
            IOE ASPIRANTS
           </div>
            <div class="link">
                  <ul>
                  <li><Link to="/" >HOME</Link></li>
                  <li><Link to="/about">ABOUT</Link></li>
                  <li><Link to="/note">NOTES</Link></li>
                  </ul>

            </div>


  </div>





</>




)


}
export default Nav
